      font-weight: 300;
    }

    a:last-child {
      margin: 1rem auto;
    	}
    }

    .nav-items.active {
      left: 100%;
    }

		button {
			margin: 0.5rem auto;
    }

    nav > a {
      margin: 1rem 0;
    }

    .hamburger {
      display: block;
      cursor: pointer;
      position: fixed;
      top: 2rem;
      right: 0.4rem;
    }

    .hamburger.active span:nth-child(1) {
      transform: translateY(8px) rotate(45deg);
    }

    hamburger.active span:nth-child(2) {
      display: none !important;
      position: absolute;
      left: 100%;
